const PostMutation = {
  createPost(parent, args, { db, request }, info) {

    const userId = getUserIdFromRequest(request);

    try {

      return db.mutation.createPost({
        data: {
          ...args.data,
          authors: { connect: [ { id: userId } ] }
        }
      }, info);

    } catch {

      throw new Error('The post could not be created');

    }

  },
  updatePost(parent, args, { db, request }, info) {

    const userId = getUserIdFromRequest(request);

    if (userId) {

      try {

        return db.mutation.updatePost({
          where: { id: args.id },
          data : { ...args.data }
        }, info);

      } catch {

        throw new Error('The post could not be updated');

      }

    }
    throw new Error('You dont own this post');

  },
  deletePost(parent, args, { db, request }, info) {

    const userId = getUserIdFromRequest(request);

    return db.mutation.deletePost({ where: { id: args.id } }, info);

  }
};

